INSERT INTO Message (id, header, content, link) VALUES(1, "Шуб-Ниггурат", "Оно нуждалось в питании — и Оно больше не будет иметь в нём недостатка. Он — Бог, а я — Верховный Жрец в Его новой жреческой иерархии. Иа! Шуб-Ниггурат! Всемогущий Козёл с Легионом Младых!", "https://ru.wikipedia.org/wiki/%D0%A8%D1%83%D0%B1-%D0%9D%D0%B8%D0%B3%D0%B3%D1%83%D1%80%D0%B0%D1%82");
INSERT INTO Message (id, header, content, link) VALUES(2, "Азатот", "Среди его эпитетов присутствуют такие как «слепой безумный бог», «вечно жующий султан демонов» и «ядерный хаос».", "https://ru.wikipedia.org/wiki/%D0%90%D0%B7%D0%B0%D1%82%D0%BE%D1%82");
INSERT INTO Message (id, header, content, link) VALUES(3, "Ньярлатхотеп", "И тогда Нъярлотхотеп вышел из Египта. Кто он, никто не знал, только был он древних туземных кровей и походил на фараона. Феллахи падали ниц, завидев его, хотя никто не мог сказать почему. Нъярлотхотеп говорил, что восстал сквозь тьму двадцати семи веков, и что голоса не с этой планеты доступны его слуху. В земли цивилизации пришел Нъярлототеп, смуглый, стройный и зловещий.", "https://ru.wikipedia.org/wiki/%D0%9D%D1%8C%D1%8F%D1%80%D0%BB%D0%B0%D1%82%D0%BE%D1%82%D0%B5%D0%BF");
INSERT INTO Message (id, header, content, link) VALUES(4, "Йог-Сотот", "Перед глазами возникли гибельные очертания таинственного Йог-Сотота — беспорядочное нагромождение переливающихся шаров, исполненных космической угрозы", NULL);
